  ListNode* removeNthFromEnd(ListNode* head, int n) {
        ListNode* slow = head;
        ListNode* fast = head;
        ListNode* pre = NULL;

        for (int i = 0; i < n; i++)
        {
            fast = fast->next;
        }

        while (fast != NULL) {
            pre = slow;
            slow = slow->next;
            fast = fast->next;
        }

        if (pre != NULL) {
            pre->next = slow->next;
            return head;
        }
        else {
            return head->next;
        }

    }
