``` c++
class Solution {
public:
    int firstMissingPositive(vector<int>& nums) {
         // 总长
        int n = nums.size();
        // 1. 全部元素没有1，返回1
        bool noOne = true;
        for(int i = 0; i < nums.size();i++){
            if(nums[i] == 1)
            {
                noOne = false;
                break;
            }
        }
        if(noOne){
            return 1;
        }

        //2. 只有一个元素，且为[1]，则返回2
        if(n == 1)
        {
            return 2;
        }

        //3. 去<= 0与 > 总数的数，将其设为1
        for(int i = 0; i < n;i++){
            if(i == n-1){
                cout << nums[i] << endl;
            }
            if(nums[i] < 1 || nums[i] > n){
                nums[i] = 1;
            }
        }


