         for(int i = 0; i < n; i++){
             int a = abs(nums[i]);
             if(a == n){
                 nums[0] = - abs(nums[0]);
             }else{
                nums[a] = - abs(nums[a]);
             }
         }

        for(int v: nums){
            cout << v << ",";
        }
        cout << endl;

        //5.遍历最新的数组中，发现有大于0，此索引就是丢失的最小正数
        for(int i = 1; i < n;i++){
            if(nums[i] > 0){
                return i;
            }
        }

        //6.5如果没有找到，第一值取出来如果是正数，则数为正数N
        if(nums[0] > 0){
            return n;
        }

        //7.如果以上走完了，返回n+1
        return n+1;
    }
};
