``` c++
 void sortColors(vector<int>& nums) {
       int n = nums.size();
        int slow0 = 0, fast = 0, slow2 = n - 1;

        while (fast <= slow2) {
            while (fast <= slow2 && nums[fast] == 2) {
                //当当前快指针为2时，移至结尾。如果换完后的nums[fast]还是2？则继续换
                nums[fast] = nums[slow2];
                nums[slow2--] = 2;
            }

            if (nums[fast] == 0) {
                nums[fast] = nums[slow0];
                nums[slow0++] = 0;
            }
            fast++;
        }
    }
