    ListNode *detectCycle(ListNode *head) {
        ListNode *slow = head;
        ListNode *fast = head;
        while(fast != NULL){
            if(fast->next == NULL)
                return nullptr;

            slow = slow->next;
            fast = fast->next->next;

            if(slow == fast){
                ListNode * ptr = head;
                while(ptr != slow){
                    ptr = ptr->next;
                    slow = slow->next;
                }
                return ptr;
            }
        }
