int maxArea(vector<int>& height) {
        int n = height.size();
        if (n < 2)
            return 0;

        int maxVolume = 0;
        int left = 0;
        int right = n - 1;

        while(left < right){
            //取左端与右端
            int lh = height[left];
            int rh = height[right];

            //计算体积
            int vol = min(lh, rh) * (right-left);
            //设置最大体积
            maxVolume = max(maxVolume, vol);

            //哪端的指针移动，取决于哪根柱子更低
            left += (lh <= rh) ? 1 : 0;
            right -= (lh > rh) ? 1 : 0;

            cout << "left:"<<left<<",right:"<<right<<endl;
        }


        return maxVolume;
    }
```


