``` c++
class Solution {
public:
    int firstMissingPositive(vector<int>& nums) {
         // 总长
        int n = nums.size();
        // 1. 全部元素没有1，返回1
        bool noOne = true;
        for(int i = 0; i < nums.size();i++){
            if(nums[i] == 1)
            {
                noOne = false;
                break;
            }
        }
        if(noOne){
            return 1;
        }

        //2. 只有一个元素，且为[1]，则返回2
        if(n == 1)
        {
            return 2;
        }

        //3. 去<= 0与 > 总数的数，将其设为1
        for(int i = 0; i < n;i++){
            if(i == n-1){
                cout << nums[i] << endl;
            }
            if(nums[i] < 1 || nums[i] > n){
                nums[i] = 1;
            }
        }


        //4.遍历，当值 == 总数。第一个值设为负数. 当值 <= 总数，以值为下标设置为负值. 特别的，当值为总数时，将第一个值为负值！
         for(int i = 0; i < n; i++){
             int a = abs(nums[i]);
             if(a == n){
                 nums[0] = - abs(nums[0]);
             }else{
                nums[a] = - abs(nums[a]);
             }
         }

        for(int v: nums){
            cout << v << ",";
        }
        cout << endl;

        //5.遍历最新的数组中，发现有大于0，此索引就是丢失的最小正数
        for(int i = 1; i < n;i++){
            if(nums[i] > 0){
                return i;
            }
        }

        //6.5如果没有找到，第一值取出来如果是正数，则数为正数N
        if(nums[0] > 0){
            return n;
        }

        //7.如果以上走完了，返回n+1
        return n+1;
    }
};
