``` c++
int bfs_sum(TreeNode* root) {
	if(root == NULL)
		return 0;
	int sum = 0;
	queue<TreeNode*> q;
	queue<int> qn;

	q.push(root);
	qn.push(root->val);

	while (!q.empty()) {
		TreeNode* node = q.front();
		int nodeVal = qn.front();
		q.pop();
		qn.pop();

		if ((node->left == NULL) && (node->right == NULL)) {
			sum += nodeVal;
		}
		else {
			if (node->left != NULL) {
				q.push(node->left);
				qn.push(nodeVal * 10 + node->left->val);
			}
			if (node->right != NULL) {
				q.push(node->right);
				qn.push(nodeVal * 10 + node->right->val);
			}
		}
	}
	return sum;
}

int dfs_sum(TreeNode* node, int prevSum) {
	if(node == NULL)
		return 0;

	int sum = prevSum * 10 + node->val;
	//左右都没有值 
	if ((node->left == NULL) &&(node->right == NULL)) {
		return sum;
	}
	else {
		sum = dfs_sum(node->left, sum) + dfs_sum(node->right, sum);
	}

	return sum;
}

int sumNumbers(TreeNode* root) {
		//return dfs_sum(root, 0);
	return bfs_sum(root);
}
```
