    vector<int> preorderTraversal(TreeNode* root) {
        vector<int> result;
        if(root == NULL)
            return result;

        stack<TreeNode> ss;
        ss.push(*root);

        while(!ss.empty()){
            TreeNode cur = ss.top();
            ss.pop();

            result.push_back(cur.val);

            if(cur.right != NULL){
                ss.push(*cur.right);
            }
            if(cur.left != NULL){
                ss.push(*cur.left);
            }
        }
        return result;
    }
```

