``` c++
class Solution {
public:
    void reverse(vector<int>& nums, int start, int end){
        for(int i = start,j = end; i < j; i++,j--){
            int ftmp = nums[j];
            nums[j] = nums[i];
            nums[i] = ftmp;
        }
    }

    void rotate(vector<int>& nums, int k) {
        if(k > nums.size()){
            k %= nums.size();
        }

        reverse(nums, 0, nums.size()-k-1);
        reverse(nums, nums.size()-k, nums.size()-1);
        reverse(nums, 0, nums.size()-1);
    }
};

```

